mapper.select:
select * from sysuser
where deleted = 0

    and username = #{username}


    and userId = #{userId}


    and status = #{status}



    and DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s')  >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%s')

mapper.update:
UPDATE sysuser
username = #{username}, salt = #{salt}, password = #{password}, phone = #{phone}, dept_id = #{deptId}, real_name = #{realName}, nick_name = #{nickName}, email = #{email}, status = #{status}, sex = #{sex}, deleted = #{deleted}, create_id = #{createId}, update_id = #{updateId}, update_time = #{updateTime}, create_where = #{createWhere}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
